import React, { Component } from "react";

class LoginForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      dropdown: "",
      radio: "",
      checkbox: "",
      date: "",
      file: "",
      slider: "",
    };
    this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
    this.dropdownChangeHandler = this.dropdownChangeHandler.bind(this);
    this.radioChangeHandler = this.radioChangeHandler.bind(this);
    this.checkboxChangeHandler = this.checkboxChangeHandler.bind(this);
    this.dateChangeHandler = this.dateChangeHandler.bind(this);
    this.fileChangeHandler = this.fileChangeHandler.bind(this);
    this.sliderChangeHandler = this.sliderChangeHandler.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  usernameChangeHandler(event) {
    this.setState({
      username: event.target.value,
    });
    console.log(event.target.value);
  }

  dropdownChangeHandler(event) {
    this.setState({
      dropdown: event.target.value,
    });
    console.log(event.target.value);
  }
  radioChangeHandler(event) {
    this.setState({
      radio: event.target.value,
    });
    //e.target.checked
    console.log(event.target.value);
  }

  checkboxChangeHandler(event) {
    this.setState({
      checkbox: event.target.value,
    });
    //checkbox: event.target.checked,
    console.log(event.target.value);
  }
  dateChangeHandler(event) {
    this.setState({
      date: event.target.value,
    });
    console.log(event.target.value);
  }

  fileChangeHandler(event) {
    this.setState({
      file: event.target.value,
    });
    console.log(event.target.value);
  }

  sliderChangeHandler(event) {
    this.setState({
      slider: event.target.value,
    });
    console.log(event.target.value);
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }
  render() {
    return (
      <React.Fragment>
        <form method="POST" className="details" onSubmit={this.handleSubmit}>
          <div className="formControl">
            <input
              type="text"
              value={this.state.username}
              onChange={this.usernameChangeHandler}
            />
          </div>
                  </form>
                  </React.Fragment>
      
    );
  }
}

export default LoginForm;
