  const [productCategory, SetProductCategory] = useState([]);

  useEffect(async () => {
    axios
      .get("http://localhost:5000/product-category")
      .then((product) => {
        SetProductCategory(product.data);
        console.log(product.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  console.log(productCategory);
