  }
  return (
    <div>
      <ul>
        {/* {productCategory.map((products) => {
                 //  alert(products);

           return <li>${products}</li>;
        })} */}

        {
      
      getparentCategories().map((products) => {
          return (
            <>
              <li key={products._id}>
                <NavLink activeClassName="active" to={products.path}>
                  {products.category}
                </NavLink>
              </li>

              {getSubCategories(products.path)}
            </>
          );
        })}
      </ul>
    </div>
  );
}

export default ProductCategory;
