import React from "react";
import { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";

import axios from "axios";

function ProductCategory() {
  const [productCategory, SetProductCategory] = useState([]);

  useEffect(async () => {
    axios
      .get("http://localhost:5000/product-category")
      .then((product) => {
        SetProductCategory(product.data);
        console.log(product.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  function getproductCategory(productCategory) {
    return (
      <ul>
        {productCategory.map((categories) => {
          console.log(categories);
          return (
            <li>
              <NavLink
                activeClassName="active"
                to={`/products/category/${categories.path.replace(/,/g, "/")}`}
              >
                {categories.categoryname}

                {
                  //categories.subcategories
                  categories.subcategories !== undefined
                    ? getproductCategory(categories.subcategories)
                    : ""
                }
              </NavLink>
            </li>
          );
        })}
      </ul>
    );
  }

  return <div>{getproductCategory(productCategory)}</div>;
}

export default ProductCategory;
