import React, { Component } from "react";

class LoginForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      dropdown: "",
      radio: "",
      checkbox: "",
      date: "",
      file: "",
      slider: "",
    };
    this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
    this.dropdownChangeHandler = this.dropdownChangeHandler.bind(this);
    this.radioChangeHandler = this.radioChangeHandler.bind(this);
    this.checkboxChangeHandler = this.checkboxChangeHandler.bind(this);
    this.dateChangeHandler = this.dateChangeHandler.bind(this);
    this.fileChangeHandler = this.fileChangeHandler.bind(this);
    this.sliderChangeHandler = this.sliderChangeHandler.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  usernameChangeHandler(event) {
    this.setState({
      username: event.target.value,
    });
    console.log(event.target.value);
  }

  dropdownChangeHandler(event) {
    this.setState({
      dropdown: event.target.value,
    });
    console.log(event.target.value);
  }
  radioChangeHandler(event) {
    this.setState({
      radio: event.target.value,
    });
    //e.target.checked
    console.log(event.target.value);
  }

  checkboxChangeHandler(event) {
    this.setState({
      checkbox: event.target.value,
    });
    //checkbox: event.target.checked,
    console.log(event.target.value);
  }
  dateChangeHandler(event) {
    this.setState({
      date: event.target.value,
    });
    console.log(event.target.value);
  }

  fileChangeHandler(event) {
    this.setState({
      file: event.target.value,
    });
    console.log(event.target.value);
  }

  sliderChangeHandler(event) {
    this.setState({
      slider: event.target.value,
    });
    console.log(event.target.value);
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }
  render() {
    return (
      <div>
        <form method="POST" className="details" onSubmit={this.handleSubmit}>
          <div className="formControl">
            <input
              type="text"
              value={this.state.username}
              onChange={this.usernameChangeHandler}
            />
          </div>
          <div className="formControl">
            <select
              name="dropdown"
              id="dropdown"
              className="dropdown"
              value={this.state.dropdown}
              onChange={this.dropdownChangeHandler}
            >
              <option value="1">1</option>
              <option value="2">2</option>
            </select>
          </div>
          <div className="formControl">
            <input
              type="radio"
              id="1"
              name="radiobutton"
              value="1"
              onChange={this.radioChangeHandler}
            />
            <label htmlFor="male">1</label>
            <input
              type="radio"
              id="2"
              name="radiobutton"
              value="2"
              onChange={this.radioChangeHandler}
            />
            <label htmlFor="female">2</label>
          </div>
          <div className="formControl">
            <input
              type="checkbox"
              id="checkbox1"
              name="vehicle1"
              value="1"
              onChange={this.checkboxChangeHandler}
            />
            <label htmlFor="vehicle1"> 1</label>
            <input
              type="checkbox"
              id="checkbox2"
              name="vehicle2"
              value="2"
              onChange={this.checkboxChangeHandler}
            />
            <label htmlFor="vehicle2">2</label>
          </div>
          <div className="formControl">
            <input
              type="date"
              id="date"
              name="selectdate"
              value={this.state.date}
              onChange={this.dateChangeHandler}
            />
          </div>
          <div className="formControl">
            <input
              type="file"
              id="uploadfile"
              name="uploadfile"
              value={this.state.file}
              onChange={this.fileChangeHandler}
            />
          </div>
          <div className="formControl">
            <input
              type="range"
              id="slider"
              name="slider"
              min="0"
              max="50"
              value={this.state.slider}
              onChange={this.sliderChangeHandler}
            />
          </div>
          <div className="formControl">
            <input type="submit" value="submit" />
          </div>
        </form>
      </div>
    );
  }
}

export default LoginForm;
