import React , {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';



function Counter (){
  let [count, setCounter]=useState(1);



  return (<>

 <h1>{count}</h1>

 <button className="add" onClick={()=>{setCounter(count++)}}>Add</button>
  <button className="delete">Delete</button> 
  </>
  );
}


function Currenttime(){
  let oldtime=new Date().toLocaleTimeString();

  const [gettime,settime]= useState(oldtime);
  console.log(gettime,oldtime)

  const updateTime=()=>{
    let update=new Date().toLocaleTimeString();
    settime(update);
  }
  return (<>gettime
  <button className="button-time" onClick={()=>{updateTime}} > Time</button>
  </>);
}


ReactDOM.render(
 <>

  <Counter/>

  <Currenttime/>



  </>
  ,document.getElementById('root')

);

